function Index(){var e=this;e.page=2,e.articleGroup=$(".articles-wrapper"),e.category_id=0}Index.prototype.clickHeaderMenuEvent=function(){$(".category-header .header-menu li").click(function(){$(this).css("border-bottom","5px solid #5c87d9").siblings().css("border-bottom","")})},Index.prototype.clickmore=function(){var a=this;$(".more").click(function(){xfzajax.get({url:"/articles/article_list/",data:{p:a.page,category_id:a.category_id},success:function(e){if(200===e.code){var t=e.data;if(0<t.length){var c=template("article-item",{articles:t});a.articleGroup.append(c),a.page+=1}else $(".more").hide()}else console.log(e.code)}})})},Index.prototype.clickcategory=function(){var r=this;$(".header-menu li").click(function(){var a=$(this).attr("data-category");console.log(a);xfzajax.get({url:"/articles/article_list/",data:{category_id:a,p:1},success:function(e){if(200===e.code){var t=e.data,c=template("article-item",{articles:t});r.articleGroup.empty(),r.articleGroup.append(c),r.page=2,r.category_id=a,$(".more").show()}}})})},Index.prototype.run=function(){var e=this;e.clickmore(),e.clickcategory(),e.clickHeaderMenuEvent()},$(function(){(new Index).run()});